mapper.select:
select count(animal_no) from heartpet_animal
where animal_tag =
#{animal_tag}

	and animal_size = #{animal_size}

	and animal_neutered = #{animal_neutered }


	and animal_species = #{animal_species }

select

from
(
select a.*, row_number() over(order by animal_no desc) as rnum
from heartpet_animal a
where animal_tag = #{animalDTO.animal_tag}

	and animal_size = #{animalDTO.animal_size }


	and animal_neutered = #{animalDTO.animal_neutered }


	and animal_species = #{animalDTO.animal_species }

)
where rnum between ${startNo} and ${endNo}

mapper.update:
update
heartpet_animal
set
animal_status = #{animal_status}
where animal_no =
#{animal_no}
