mapper.select:
select count(animal_no) from heartpet_animal
where animal_tag =
#{animalDTO.animal_tag} and  animal_state >= #{animalDTO.animal_state}

	and animal_size = #{animalDTO.animal_size }


	and animal_neutered = #{animalDTO.animal_neutered }


	and animal_species = #{animalDTO.animal_species }


	and animal_age between (${animalDTO.animal_age } -1) and ${animalDTO.animal_age }


	and animal_age >= 5 


	and animal_gender = #{animalDTO.animal_gender }


	and animal_weight >= ${animalDTO.animal_weight} 


	and animal_status = #{animalDTO.animal_status}


	and animal_place like '%' || #{animalDTO.animal_place } || '%'


	and animal_place like '%' || #{keyword } || '%'

select

from
(
select a.*, row_number() over(order by animal_no desc) as rnum
from heartpet_animal a
where animal_tag = #{animalDTO.animal_tag} and  animal_state >= #{animalDTO.animal_state}

	and animal_size = #{animalDTO.animal_size }


	and animal_neutered = #{animalDTO.animal_neutered }


	and animal_species = #{animalDTO.animal_species }


	and animal_age between (${animalDTO.animal_age } -1) and ${animalDTO.animal_age }


	and animal_age >= 5 


	and animal_gender = #{animalDTO.animal_gender }


	and animal_weight >= ${animalDTO.animal_weight} 


	and animal_status = #{animalDTO.animal_status}


	and animal_place like '%' || #{animalDTO.animal_place } || '%'


	and animal_place like '%' || #{keyword } || '%'

)
where rnum between ${startNo} and ${endNo}

mapper.update:
update
heartpet_animal
set
animal_status = #{animal_status}
where animal_no =
#{animal_no}
